mapper.select:
select a.* /* StatMapper.selectStatList :: 스탯 리스트 조회 */
    , (select b.NAME from stat b where a.BASE_STAT_ID = b.ID) as base_stat_name
from stat a


        stat_type = #{statType}


        and (
        (
            /* 초성검색 */
            a.name >= #{searchWord.startWord}
            and a.name <= #{searchWord.endWord}
        )
            /* 일반 like 검색 */
            or a.name like ('%'||#{name}||'%')
        )

select * /* StatMapper.selectStat :: 스탯 상세 조회 */
from stat

    id = #{id}

mapper.insert:
insert into stat /* StatMapper.insertStat :: 스탯 등록 */
(
    name
    , txt
    , show_yn
    , stat_type
    , min_num
    , max_num
    , default_num
    , unit
    , calc_type
    , base_stat_id
    , calc_txt
)
values (
    #{name}
    , #{txt}
    , #{showYn}
    , #{statType}
    , #{minNum}
    , #{maxNum}
    , #{defaultNum}
    , #{unit}
    , #{calcType}
    , #{baseStatId}
    , #{calcTxt}
)

mapper.update:
update stat /* StatMapper.updateStat :: 스탯 수정 */
set
    name = #{name}
  , txt = #{txt}
  , show_yn = #{showYn}
  , stat_type = #{statType}
  , min_num = #{minNum}
  , max_num = #{maxNum}
  , default_num = #{defaultNum}
  , unit = #{unit}
  , calc_type = #{calcType}
  , base_stat_id = #{baseStatId}
  , calc_txt = #{calcTxt}
where id = #{id}
